#!/usr/bin/env python3
#Question1
dna = ''
nt_count={}
for nt in dna:
    if nt in nt_count:
        nt_count[nt] += 1
    else:
        nt_count[nt] = 1
print(nt_count)
#Calculating GC conent 
G = nt_count['G']
C = nt_count['C']
A = nt_count['A']
T = nt_count['T']
GC_content = (G+C)/(G+C+A+T)
print(f'% GC content is {GC_content:.0%}')
